// 获取输入数据（来自AI Agent的prompt）
const prompt = query;

// 验证输入
if (!prompt) {
  throw new Error("缺少图片描述参数 'prompt'");
}

// 默认参数配置
const width = 512;
const height = 512;
const model = "flux";

// 构建 API URL - 强制 nologo=true
let url = `https://image.pollinations.ai/prompt/${encodeURIComponent(prompt)}?`;
url += `width=${width}&height=${height}&model=${model}&nologo=true`;

// 添加随机种子确保结果多样性
const seed = Date.now() % 1000000;
url += `&seed=${seed}`;

// 返回结果给AI Agent
return url;

// 获取输入数据
const inputString = $input.first().json.body.markdown;

// 按 # 分割字符串
const parts = inputString.split("---");

// 创建新 items
return parts.map(part => {
  return {
    json: {
      segment: part.trim(),  // 移除首尾空格
      length: part.length
    },
    // 可选：保留原始数据
    pairedItem: $item.index
  };
});

// 遍历所有输入的 Item（$input.all() 返回所有独立 Item）
const processedItems = $input.all().map((item, index) => {
  // 获取当前 Item 中的 Markdown 文本（字段名为 output）
  const rawMarkdown = item.json.output;

  // 将 \n 转义符替换为真实换行符
  const processedMarkdown = rawMarkdown.replace(/\\n/g, '\n');

  // 返回处理后的 Item
  return {
    json: {
      processedMarkdown,
      itemIndex: index // 可选：标记当前 Item 的索引
    }
  };
});

// 返回所有处理后的 Item
return processedItems;

