const items = $input.all();
const globalHelpers = this.helpers;

// --- 配置 ---
const ENDPOINT = 'https://api.deepseek.com/v1/chat/completions';
const TOKEN = 'Bearer sk-6cb5f4f271a9459fad010a78940671b7';
const CONCURRENCY = 10;  // 最大并发
const TIMEOUT = 450000;   // 30秒超时
const RETRIES = 2;       // 最多重试2次
const sysMessage1 = "传入的内容是有关ppt每一页非常简略的大纲，请为每一个要点增添详细的内容，不要大纲式的，要有一段段文字，但要保证一页的总字数不超过150，不要在结尾用括号提示字数，请在需要添加图片的地方，把图片描述写在中括号中，不要写在正文中，每一页最多一张图片，注意，如果说明这一页是目录，则保持大纲式结构。";
const sysMessage2 = "传入的是ppt的内容，不要修改正文文字内容，请形成一个ppt（markdown形式），在中括号提示有图片的地方一定要根据文本信息写详细的英文prompt（prompt将被传给生图工具）并将该prompt写入[]之间，没有中括号提示加图的地方不要加图。给我返回markdown文件，不要一开始的```markdown和结尾的```，最后这个文件要给到slidev，生成幻灯片。";

// --- 图片生成函数 ---
function generateImageUrl(prompt) {
    const width = 200;
    const height = 200;
    const model = "flux";
    const seed = Date.now() % 1000000; // 随机种子

    // 构建图片URL（带随机种子避免缓存）
    let url = `https://image.pollinations.ai/prompt/${encodeURIComponent(prompt)}?`;
    url += `width=${width}&height=${height}&model=${model}&nologo=true`;
    url += `&seed=${seed}`;

    return url;
}

// --- 单条请求函数（带重试）---
async function callDeepSeek(sysMessage, content, attempt = 0) {
    try {
        const res = await globalHelpers.httpRequest({
            method: 'POST',
            url: ENDPOINT,
            body: {
                model: 'deepseek-chat',
                messages: [{
                    role: "system",
                    content: sysMessage
                }, {
                    role: "user",
                    content: content
                }],
            },
            json: true,
            headers: {
                'Content-Type': 'application/json',
                'Authorization': TOKEN
            },
            timeout: TIMEOUT
        });

        return {
            json: {
                apiResponse: res,
                success: true
            },
        };

    } catch (err) {
        if (attempt < RETRIES) {
            await new Promise(r => setTimeout(r, 500 * (attempt + 1)));
            return callDeepSeek(sysMessage, content, attempt + 1);
        }
        return {
            json: {
                success: false,
                error: err.message || 'API请求失败'
            },
        };
    }
}

// --- 处理图片标记 ---
async function processImages(markdown) {
    // 正则匹配所有图片描述 [description]
    const regex = /\[([^\]]+)\]/g;
    let match;
    const replacements = [];

    // 收集所有需要替换的图片标记
    while ((match = regex.exec(markdown)) !== null) {
        const fullMatch = match[0];
        const description = match[1];
        replacements.push({ fullMatch, description });
    }

    // 并行生成所有图片URL
    const imageUrls = await Promise.all(
        replacements.map(async (item) => {
            return {
                ...item,
                url: `${generateImageUrl(item.description)}`
            };
        })
    );

    // 执行替换操作
    let processedMarkdown = markdown;
    for (const replacement of imageUrls) {
        processedMarkdown = processedMarkdown.replace(
            replacement.fullMatch,
            `![](${replacement.url})`
        );
    }

    return processedMarkdown;
}

// --- 并发控制器（保持顺序）---
async function processAll(items, concurrency) {
    const results = new Array(items.length);
    let index = 0;

    async function worker() {
        while (index < items.length) {
            const currentIndex = index++;
            const item = items[currentIndex];

            try {
                // 第一步：生成详细内容
                const result1 = await callDeepSeek(sysMessage1, item.json.segment);
                if (!result1.json.success) {
                    results[currentIndex] = { 
                        json: { error: `第一步失败: ${result1.json.error}` } 
                    };
                    continue;
                }

                // 第二步：生成markdown结构
                const result2 = await callDeepSeek(
                    sysMessage2, 
                    result1.json.apiResponse.choices[0].message.content
                );

                if (!result2.json.success) {
                    results[currentIndex] = { 
                        json: { error: `第二步失败: ${result2.json.error}` } 
                    };
                    continue;
                }

                // 提取markdown内容
                let markdown = result2.json.apiResponse.choices[0].message.content;

                // 第三步：处理图片标记
                const processedMarkdown = await processImages(markdown);

                // 保存最终结果
                results[currentIndex] = {
                    json: {
                        success: true,
                        markdown: processedMarkdown,
                    }
                };

            } catch (error) {
                results[currentIndex] = { 
                    json: { error: `处理失败: ${error.message}` } 
                };
            }
        }
    }

    // 启动工作线程
    const workers = Array.from({ length: Math.min(concurrency, items.length) }, worker);
    await Promise.all(workers);
    return results;
}

// --- 执行主流程 ---
try {
    const processed = await processAll(items, CONCURRENCY);
    return processed;
} catch (error) {
    return [{ json: { error: "处理流程失败: " + error.message }}];
}

