        success: true          // 成功标记
      },
    };

  } catch (err) {
    if (attempt < RETRIES) {
      await new Promise(r => setTimeout(r, 500 * (attempt + 1)));
      return callDeepSeek(item, attempt + 1);
    }
    return {
      json: {
